import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";

function APP() {
  return (
    <div className="container">
      <Header />
      <Menu />
      <Footer />
    </div>
  );
}

function Header() {
  const style = {};

  return (
    <header className="header">
      <h1 style={style}>Fast React Pizza Co.</h1>;
    </header>
  );
}

function Menu() {
  return (
    <div className="menu">
      <h2>Our Menu</h2>
      <Pizza />
      <Pizza />
      <Pizza />
    </div>
  );
}

function Footer() {
  const time = new Date().toLocaleTimeString();
  const hour = new Date().getHours();
  const openHour = 12;
  const closeHour = 22;

  return (
    <footer>
      <p>Copyright 2022</p>
    </footer>
  );
}

const Test = () => {
  return (
    <div>
      <h1>Test</h1>
      <Pizza />
    </div>
  );
};

function Pizza() {
  return (
    <div>
      <img src="pizzas/spinaci.jpg" alt="Pizza spinaci" />
      <h2>Pizza</h2>
      <p>Tomato, mozarella, spinach, and ricotta cheese.</p>
    </div>
  );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <APP />
  </React.StrictMode>
);
